def getAttachment(bs):
    attachment = {}
    attach_dom = bs.find(string="相关资料下载：")
    if attach_dom is None:
        return None
    attach_dom = attach_dom.find_next()
    attachment['href'] = attach_dom['href']
    attachment['filename'] = ILLEGAL_CHARACTERS(attach_dom.string)
    return attachment


def Page1(urlEnd, index):
    urlPrefix = "http://www.sccin.com/InvestmentInfo/ZhaoBiao/"
    url = f"{urlPrefix}{urlEnd}"
    ret = {}
    if index is not None:
        tip = f'第{index + 1}条数据, {url}: '
    else:
        tip = f"修补数据,{url}： "
    try:
        headers = {
            "Cookie": cookie
        }
        res = get(url, headers)
        bs = BeautifulSoup(res.content, features="html.parser", from_encoding="UTF-8")
        res.close()
        titles = bs.select('html > head > title')
        if not titles:
            printTip(f'titles empty, {url}')
            return None
        if ILLEGAL_CHARACTERS(titles[0].string) == "四川建设网 > 用户验证":
            printTip(f'redirect login, {url}')
            return None
        title = titles[1].string
        title = ILLEGAL_CHARACTERS(title)
        ret['title'] = title
        if title == '':
            # 说明是空页面
            return None
        content = bs.find(class_="sanji_con").get_text()
        ret['content'] = ILLEGAL_CHARACTERS(content)
        ret['attachment'] = getAttachment(bs)
        printSuccess(f'{tip} 爬取')
        ret['isError'] = False
    except Exception as e:
        printError(f'获取页面失败, {url}, {e}')
        ret['isError'] = True
    else:
        ret['type'] = 1
        ret['url'] = url
        return ret


def getSinglePageInfo(urlEnd, index=None):
    ret = Page1(urlEnd, index)
    return ret


def getPages_(bs):
    ret = []
    lists = bs.select("#ContentPlaceHolder1_GVList tr")
    if lists is not None:
        for i in range(1, len(lists)):
            item = lists[i]
            if item is not None:
                t = {}
                t['href'] = item.find('a')['href']
                t['province'] = ILLEGAL_CHARACTERS(item.find('span').string)
                t['time'] = ILLEGAL_CHARACTERS(item.select('td')[-1].string)
                ret.append(t)
    return ret


def getPages(key, page=1):
    ret = {}
    try:
        tip = f'爬取关键字为{key}第{page}页的url'
        url = 'http://www.sccin.com/InvestmentInfo/ZhaoBiao/InvitNotice.aspx?typeid=0&&type=ZBGG'
        data = make_json(dataStr)
        data['ctl00$ContentPlaceHolder1$txtProjectnames'] = key
        data['__EVENTARGUMENT'] = page
        res = post(url, data=data)
        bs = BeautifulSoup(res.content, features="html.parser", from_encoding="UTF-8")
        res.close()
        if page == 1:
            splits = bs.find(id="ContentPlaceHolder1_myPager").find('div', recursive=False).get_text().split('，')
            page_number = splits[1]
            page_number = re_strip(page_number, r'[^\d]')
            ret['page_number'] = int(page_number)
        ret['content'] = getPages_(bs)
        printSuccess(tip)
    except Exception as e:
        printError(f"爬取首页{url}, error: {e}")
        ret = None
    else:
        return ret


def getUrl(key):
    dic = f'./files/{key}/'
    pages = getPages(key)
    page_number = pages['page_number']
    result = pages['content']
    args = []
    for i in range(2, page_number + 1):
        args.append((key, i))
    res = pool(args, getPages, 20)
    for item in res:
        result.extend(item['content'])
    if not os.path.exists(dic):
        os.mkdir(dic)
    writeToFile(f'{dic}url.json', result)
    return result


def login():
    res = get('http://www.sccin.com/common/LoginHandler.ashx?651&op=logined', headers={"Cookie": cookie})
    return res.content


def getAllPagesInfo(key):
    # data = getUrl(key)
    dic = f'./files/{key}/'
    data = readFromFile(f'{dic}url.json')
    result = []
    for i in range(len(data)):
        item = getSinglePageInfo(data[i]['href'], i + 1)
        if item is not None:
            item['province'] = data[i]['province']
            item['time'] = data[i]['time']
            result.append(item)
        import time
        time.sleep(1)

    writeToFile(f'{dic}pages.json', result)


